import React, { Component } from 'react';
import ReactDOM from 'react-dom';

import List from './List';

const the_items = [
	{'name': 'Kevin Burgs',
	 'tagname': 'K_BurgsRulezz',
	 'text': 'I LOVE to bath in LOSER TEARS!!',
	 'location': 'Spacey, FL'
	},
	{'name': 'Mike Swanson',
	 'tagname': 'hardhittz_M',
	 'text': 'long journey ahead. Offroaders',
	 'location':'Fremont, CA'
	}
];
class Tweets extends Component {
	constructor(props) {
		super(props);
		this.state = {
			text: undefined
		}
	}


	        componentDidMount() {
			               this.callBackendAPI()
					.then(res => this.setState({ text: res.statuses }))
			                .catch(err => console.log(err));
			        }
	        callBackendAPI = async () => {
			                const response = await fetch('/express_backend')
			                const body = await response.json();
					console.log(body.statuses[0]);
			                if (response.status !== 200) {
						                        throw Error(body.message)
						                }
			                return body;
			        }

	render () {
		if (!this.state.text) return <p>Loading...</p>
		return (
			<p>{this.state.text.statuses[0].text}</p>	
		)
	}
}

export default Tweets
